            ...doc.data(),
            id: doc.id,
        }));

        const cityQuerySnapshot = await firebase
        .firestore()
        .collection("city")
        .get();
    const cities = cityQuerySnapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
    }));
        return {job : jobs, area : areas, city : cities};
