import {loadFirebase} from '../lib/db'
export default class AreaInput extends React.Component{
    static async getInitialProps() {
        let firebase = await loadFirebase() 
        let result2 = await new Promise((resolve, reject) => {
            firebase.firestore().collection('area')
              .limit(10)
              .get()
              .then(snapshot => {
                console.log(snapshot)
                let data2 = []
                snapshot.forEach((doc) => {
                  data2.push(
                    Object.assign({
                      id: doc.id,
                      data : doc.data()
                    })
                  )
                })
                console.log("data2")
                resolve(data2)
              })
              .catch(error => {
                reject([])
              })
          })
          return {area: result2}
      }
    state = {
        cities : [
            "Tokyo", 
            "Osaka", 
            "Hiroshima",
            "Nagoya", 
            "Nagano", 
            "Kyoto", 
            "Sendai", 
            "Sakata", 
            "Kanazawa", 
            "Kobe", 
            "Yokohama", 
            "Akita", 
            "Aomori", 
            "Hirosaki", 
            "Hachinohe", 
            "Fukushima", 
            "Morioka", 
            "Niigata", 
            "Kure", 
            "Matsue", 
            "Tottori", 
            "Gifu", 
            "Tsu", 
            "Matsumoto", 
            "Shizuoka", 
            "Hamamatsu", 
            "Fukui", 
            "Maizuru", 
            "Wakayama", 
            "Mito", 
            "Kofu", 
            "Iwaki", 
            "Nagaoka", 
            "Yamagata", 
            "Tsuruoka", 
            "Okayama", 
            "Shimonoseki", 
            "Toyama", 
            "Takaoka", 
            "Otsu", 
            "Maebashi", 
            "Kawasaki", 
            "Kawagoe", 
            "Utsunomiya", 
            "Hachioji", 
            "Koriyama"
        ],
    }
    handleChange = (event)=>{
        console.log('calling handlechange')
        this.setState({[event.target.name] : event.target.value})
        console.log(event.target.value)
    }
    handleSubmit = (event) => {
        console.log(this.state)
        event.preventDefault()
        for(var i = 0;i<this.state.cities.length;i++){
            try{
                let firebase = loadFirebase() 
                firebase.firestore().collection('city').add({cityName : this.state.cities[i], AREAID : this.state.area})
            }catch (error){
                console.log(error)
            }
        }
    } 
    render (){
        const area = this.props.area
        console.log(area)
        return (
            <form>
                <select name="area" onChange={this.handleChange}>
                    {area && area.map(Area => (
                        <option value={Area.id}>{Area.data.areaName}</option>
                    ))}
                </select>
                <button onClick={this.handleSubmit}>Submit</button>
            </form>
        )
    }
}
