import {loadFirebase} from '../lib/db'
export default class AreaInput extends React.Component{
    static async getInitialProps() {
        let firebase = await loadFirebase() 
        let result2 = await new Promise((resolve, reject) => {
            firebase.firestore().collection('area')
              .limit(10)
              .get()
              .then(snapshot => {
                console.log(snapshot)
                let data2 = []
                snapshot.forEach((doc) => {
                  data2.push(
                    Object.assign({
                      id: doc.id,
                      data : doc.data()
                    })
                  )
                })
                console.log("data2")
                resolve(data2)
              })
              .catch(error => {
                reject([])
              })
          })
          return {area: result2}
      }
    state = {
        cities : [
            "Sapporo",
            "Asahikawa",
            "hakodate",
            "kushiro",
            "tomakomai",
            "obihiro",
            "otaru",
            "kitami",
            "ebetsu",
            "chitose",
            "Muroran",
            "iwamizawa",
            "Eniwa",
            "Kitahiroshima",
            "ishikari",
            "noboribetsu",
            "hokuto",
            "otofuke",
            "takikawa",
            "abashiri",
            "wakkanai",
            "date",
            "nayoro",
            "nanae",
            "nemuro",
            "makubetsu",
            "nakashibetsu",
            "shinhidaka",
            "mombetsu",
            "bibai",
            "furano",
            "Rumoi",
            "Fugawa",
            "Engaru",
            "Bihoro",
            "Shibetsu",
            "kushiro",
            "yoichi",
            "memuro",
            "shiraoi",
            "sunagawa",
           "tobetsu",
            "yakumo",
            "mori",
            "betsukai",
            "kutchan",
            "ashibetsu",
            "urakawa",
            "lwanai",
            "hidaka",
            "kuriyama",
            "shari",
            "akabira",
            "naganuma",
            "kamifurano",
            "biei",
            "higashikagura"
        ],
    }
    handleChange = (event)=>{
        console.log('calling handlechange')
        this.setState({[event.target.name] : event.target.value})
        console.log(event.target.value)
    }
    handleSubmit = (event) => {
        console.log(this.state)
        event.preventDefault()
        for(var i = 0;i<this.state.cities.length;i++){
            try{
                let firebase = loadFirebase() 
                firebase.firestore().collection('city').add({cityName : this.state.cities[i], AREAID : this.state.area})
            }catch (error){
                console.log(error)
            }
        }
    } 
    render (){
        const area = this.props.area
        console.log(area)
        return (
            <form>
                <select name="area" onChange={this.handleChange}>
                    {area && area.map(Area => (
                        <option value={Area.id}>{Area.data.areaName}</option>
                    ))}
                </select>
                <button onClick={this.handleSubmit}>Submit</button>
            </form>
        )
    }
}
