import React from "react";
import Link from "next/link";
import Head from "next/head";
import { loadFirebase } from "../lib/db.js";

export default class employerList extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.datatableRef = React.createRef();
    // this.employers = props.employer || []
    this.$datatable = null
    this.state = {
        C_Name : '',
        C_noEmp: '',
        C_published_date:'',
        C_tel:'',
        C_emailaddr:'',
        C_city:'',
        C_area:'',
        C_Address:'',
        C_Description:'',
 employers: props.employer || [],
    };
  }

  componentDidMount() {
      this.initializeDatatable()
    // this.$datatable = $(this.datatableRef.current).DataTable({
    //   order: [[3, "desc"]],
    // });
  }
  
  initializeDatatable() {
    this.$datatable = $(this.datatableRef.current).DataTable({
        order: [[3, "desc"]],
      });
  }

  refreshTable() {
    this.$datatable.clear()
    const self = this;
    employerList
      .getInitialProps()
      .then((response) => {
        self.setState({
          employers: response.employer,
        });
        // self.initializeDatatable()
      })
      .bind(this);
  }

  static async getInitialProps() {
    let firebase = await loadFirebase();
    let result = await new Promise((resolve, reject) => {
      firebase
        .firestore()
        .collection("employer")
        .limit(10)
        .get()
        .then((snapshot) => {
          console.log(snapshot);
          let data = [];
          snapshot.forEach((doc) => {
            data.push(
              Object.assign(
                {
                  id: doc.id,
                },
                doc.data()
              )
            );
          });
          console.log(data);
          resolve(data);
        })
        .catch((error) => {
          reject([]);
        });
    });
    let result2 = await new Promise((resolve, reject) => {
      firebase
        .firestore()
        .collection("area")
        .limit(10)
        .get()
        .then((snapshot) => {
          console.log(snapshot);
          let data2 = [];
          snapshot.forEach((doc) => {
            data2.push(
              Object.assign(
                {
                  id: doc.id,
                },
                doc.data()
              )
            );
          });
          console.log(data2);
          resolve(data2);
        })
        .catch((error) => {
          reject([]);
        });
    });
    return { employer: result, area: result2 };
  }

  deletePassId = (id) => {
    $("#deleteConfirmModal").modal("show");
    this.setState({ delete_id: id });
    console.log(this.state.delete_id);
  };

  viewPassId = (id) => {
    this.setState({ edit_id: id });
    let firebase = loadFirebase();
    let data = {};
    try {
      firebase
        .firestore()
        .collection("employer")
        .doc(id)
        .get()
        .then((snapshot) => {
          data = snapshot.data();
          console.log(data);
          this.setState({
            C_Name :data.C_Name,
            C_noEmp:data.C_noEmp,
            C_published_date:data.C_published_date,
            C_tel:data.C_tel,
            C_emailaddr:data.C_emailaddr,
            C_city:data.C_city,
            C_area:data.C_area,
            C_Address:data.C_Address,
            C_Description:data.C_Description,
          });
        });
      console.log("getting data");
      console.log(this.state.employerName);
    } catch (error) {
      console.log(error);
    }
    console.log(this.state.employerName);
    $("#viewModal").modal("show");
  };

  editPassId = (id) => {
    this.setState({ edit_id: id });
    let firebase = loadFirebase();
    let data = {};
    try {
      firebase
        .firestore()
        .collection("employer")
        .doc(id)
        .get()
        .then((snapshot) => {
          data = snapshot.data();
          console.log(data);
          this.setState({
            C_Name :data.C_Name,
            C_noEmp:data.C_noEmp,
            C_published_date:data.C_published_date,
            C_tel:data.C_tel,
            C_emailaddr:data.C_emailaddr,
            C_city:data.C_city,
            C_area:data.C_area,
            C_Address:data.C_Address,
            C_Description:data.C_Description,
          });
        });
      console.log("getting data");
      console.log(this.state.employerName);
    } catch (error) {
      console.log(error);
    }
    console.log(this.state.employerName);
    $("#editModal").modal("show");
  };

  updateEmployer = () => {
    let firebase = loadFirebase();
    try {
      firebase
        .firestore()
        .collection("employer")
        .doc(this.state.edit_id)
        .update({
            C_Name:this.state.C_Name,
            C_noEmp:this.state.C_noEmp,
            C_published_date:this.state.C_published_date,
            C_tel:this.state.C_tel,
            C_emailaddr:this.state.C_emailaddr,
            C_city:this.state.C_city,
            C_area:this.state.C_area,
            C_Address:this.state.C_Address,
            C_Description:this.state.C_Description,
        });
      this.refreshTable();
    } catch (error) {
      console.log(error);
    }
  };

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  deleteEmployer = async () => {
    let firebase = loadFirebase();
    try {
       await firebase
        .firestore()
        .collection("employer")
        .doc(this.state.delete_id)
        .delete();
      console.log("Delete successful");
      this.refreshTable();
    } catch (error) {
      console.log(error);
    }
  };

  render() {
    // const employer = this.props.employer;
    const area = this.props.area;
    return(
            
        <html>    
            <body className="sb-nav-fixed">
                <nav className="sb-topnav navbar navbar-expand navbar-dark bg-dark">
                    <a className="navbar-brand" href="dashboard">Admin</a>
                    <button className="btn btn-link btn-sm order-1 order-lg-0" id="sidebarToggle" href="#"><i className="fas fa-bars"></i></button>
                
                    <form className="d-none d-md-inline-block form-inline ml-auto mr-0 mr-md-3 my-2 my-md-0">
                        <div className="input-group">
                            <ul className="navbar-nav ml-auto ml-md-0">
                                <li className="nav-item dropdown">
                                    <a className="nav-link dropdown-toggle" id="userDropdown" href="#" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"><i className="fas fa-user fa-fw"></i></a>
                                    <div className="dropdown-menu dropdown-menu-right" aria-labelledby="userDropdown">
                                        <a className="dropdown-item" href="#">Profile</a>
                                        <a className="dropdown-item" href="#">Activity Log</a>
                                        <div className="dropdown-divider"></div>
                                        <a className="dropdown-item" href="login">Logout</a>
                                    </div>
                                </li>
                            </ul>
                        </div>
                    </form>
                   
                   
                </nav>
                <div id="layoutSidenav">
                    <div id="layoutSidenav_nav">
                        <nav className="sb-sidenav accordion sb-sidenav-dark" id="sidenavAccordion">
                            <div className="sb-sidenav-menu">
                                <div className="nav">
                                    <div className="sb-sidenav-menu-heading">Core</div>
                                    <a className="nav-link" href="dashboard">
                                        <div className="sb-nav-link-icon"><i className="fas fa-tachometer-alt"></i></div>
                                        Dashboard
                                    </a>
                                   
                                    <a className="nav-link collapsed" href="#" data-toggle="collapse" data-target="#collapsePages" aria-expanded="false" aria-controls="collapsePages">
                                        <div className="sb-nav-link-icon"><i className="fas fa-book-open"></i></div>
                                        Pages
                                        <div className="sb-sidenav-collapse-arrow"><i className="fas fa-angle-down"></i></div>
                                    </a>
                                    <div className="collapse" id="collapsePages" aria-labelledby="headingTwo" data-parent="#sidenavAccordion">
                                        <nav className="sb-sidenav-menu-nested nav accordion" id="sidenavAccordionPages">
                                            <a className="nav-link collapsed" href="#" data-toggle="collapse" data-target="#pagesCollapseAuth" aria-expanded="false" aria-controls="pagesCollapseAuth">
                                                Authentication
                                                <div className="sb-sidenav-collapse-arrow"><i className="fas fa-angle-down"></i></div>
                                            </a>
                                            <div className="collapse" id="pagesCollapseAuth" aria-labelledby="headingOne" data-parent="#sidenavAccordionPages">
                                                <nav className="sb-sidenav-menu-nested nav">
                                                    <a className="nav-link" href="login">Login</a>
                                                    <a className="nav-link" href="register">Register</a>
                                                    <a className="nav-link" href="password">Forgot Password</a>
                                                </nav>
                                            </div>
                                           
                                        </nav>
                                    </div>
                                </div>    
                            </div>
                            
                        </nav>
                    </div>
                    <div id="layoutSidenav_content">
                        <main>
                            <div className="container-fluid">
                                <h1 className="mt-4">Employer Tables</h1>
                                <div className="row">
                                <div className="col-xl-3 col-md-6">
                                        <div className="card bg-primary text-white mb-4">
                                            <div className="card-body">Jobs table</div>
                                            <div className="card-footer d-flex align-items-center justify-content-between">
                                                <a className="small text-white stretched-link" href="dashboard">View mores</a>
                                                <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="col-xl-3 col-md-6">
                                        <div className="card bg-warning text-white mb-4">
                                            <div className="card-body">New Job</div>
                                            <div className="card-footer d-flex align-items-center justify-content-between">
                                                <a className="small text-white stretched-link" href="AddJob">+Add New</a>
                                                <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                            </div>
                                        </div>
                                    </div>
                                   
                                    <div className="col-xl-3 col-md-6">
                                        <div className="card bg-danger text-white mb-4">
                                            <div className="card-body">New Employer</div>
                                            <div className="card-footer d-flex align-items-center justify-content-between">
                                                <a className="small text-white stretched-link" href="AddEmployer">+Add New</a>
                                                <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                            </div>
                                        </div>
                                    </div>
                                   
                                    
                                </div>
                               
                                <div className="card-body">
                                   
                                   
                                   
                                <div className="table-responsive">
                                        <table className="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                                            <thead>
                                            <tr>
                                                    <th>Employer Name</th>
                                                     <th>Phone</th>
                                                    <th>Email</th>
                                                    <th>Area</th>
                                                    <th>City</th>
                                                    <th style={{textAlign:"center"}}>Action</th>
                                                    
                                                </tr>
                                            </thead>
                                          
                                            <tbody>
                                               {employer &&  employer.map(Employer => (
                                                <tr id={Employer.id}>
                                                <td>{Employer.C_Name}</td>
                                                <td>{Employer.C_tel}</td>
                                               <td>{Employer.C_emailaddr}</td>
                                                <td>{Employer.C_area}</td>
                                                <td>{Employer.C_city}</td>
                                             
                                                <td>
                                        <a><i onClick={()=>this.viewPassId(Employer.id)} className="view material-icons icon-padding" title="View" style={{color: "rgb(0, 110, 255)", cursor:"pointer"}} data-dismiss="modal" data-toggle="tooltip">&#xE417;</i></a> &nbsp;
                                        <a><i onClick={()=>this.editPassId(Employer.id)} className="edit material-icons icon-padding" title="Edit" style={{color: "yellow", cursor:"pointer"}} data-dismiss="modal" data-toggle="tooltip">&#xE254;</i></a> &nbsp;
                                        <a onClick={()=>this.deletePassId(Employer.id)} className="delete material-icons icon-padding" title="Delete"  style={{color: "red", cursor:"pointer"}} data-dismiss="modal">&#xE872;</a> &nbsp;
                                    </td>
                                                </tr>
                                                ))
                                                }
                                            </tbody>
                                        </table>                        
                                    </div>                       
                                    
                          </div>
                          <div id="viewModal" className="modal fade">
                <div className="modal-dialog info-dialog">
                    <div className="modal-content">
                            <div className="modal-header">						
