import React from 'react';
import Link from 'next/link';
import Head from 'next/head';
import {loadFirebase} from '../lib/db.js';

export default class jobList extends React.Component {

    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.datatableRef = React.createRef();
        this.$datatable = null
        this.state = {
            edit_id : '',
            delete_id : '',
            Jname:'',
            J_tel:'',
            J_min_salary:'',
            J_max_salary:'',
            PostedDate:'',
            J_hours:'',
            J_email:'',
            J_city:'',
            J_area:'',
            J_Working_Day:'',
            J_Qualification:'',
            J_JpLevel:'',
            J_Description:'',
            J_Contract:'',
            J_Address:'',
            areaName : '',
            jobs : props.job || [],
            areas : props.area || [],
        };
    }
    componentDidMount() {
        this.initializeDatatable()
    }

    initializeDatatable() {
      this.$datatable = $(this.datatableRef.current).DataTable({
          order: [[3, "desc"]],
        });
    }

    refreshTable() {
      this.$datatable.clear()
      const self = this;
      jobList
        .getInitialProps()
        .then((response) => {
          self.setState({
            jobs: response.job,
          });
          // self.initializeDatatable()
        })
        .bind(this);
    }

    static async getInitialProps() {
        const firebase = await loadFirebase() 
        const jobQuerySnapshot = await firebase
            .firestore()
            .collection("job")
            .get();
        const jobs = jobQuerySnapshot.docs.map((doc) => ({
            ...doc.data(),
            id: doc.id,
        }));
        const areaQuerySnapshot = await firebase
            .firestore()
            .collection("area")
            .get();
        const areas = areaQuerySnapshot.docs.map((doc) => ({
            ...doc.data(),
            id: doc.id,
        }));

        const cityQuerySnapshot = await firebase
        .firestore()
        .collection("city")
        .get();
    const cities = cityQuerySnapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
    }));
        return {job : jobs, area : areas, city : cities};
    }

    // getCities = (id) => {
    //     let cities = []
    //     try{
    //         let firebase = loadFirebase()
    //         firebase.firestore().collection('city').where('AREAID',"==",id).get()
    //         .then((cityQuerySnapshot)=> {
    //             cityQuerySnapshot.forEach(doc => {
    //                 cities.push(Object.assign(
    //                     {id : doc.id},
    //                     doc.data()
    //                 ))
    //             })
    //             console.log({cities})
    //             this.setState({cities})
    //         })
    //     }catch(error){
    //         console.log(error)
    //     }
    // };
    deletePassId = (id)=>{
        $("#deleteConfirmModal").modal('show');
        this.setState({delete_id : id})
        console.log(this.state.delete_id)
    };      
        viewPassId = (id) => {
            this.setState({edit_id : id})
            let firebase = loadFirebase()
            let data = {}
            try{
                firebase.firestore().collection('job').doc(id).get().
                then((snapshot)=>{
                    data = snapshot.data()
                    console.log(data)
                    this.setState ({
                       
                Jname:data.Jname,
