     getLocation = (CITYID, AREAID) => {
        const city = this.props.city
        const area = this.props.area
        let cityName = ''
        let areaName = ''

        city && city.map(City=>{
            if(City.id == CITYID){
                cityName = City.cityName
            }
        })
        area && area.map(Area=>{
            if(Area.id == AREAID){
                areaName = Area.areaName
            }
        })
       return cityName + "," + areaName
     }

    getArea = (id) => {
