import React from 'react';
import Link from 'next/link'
import {loadFirebase} from '../lib/db.js';


export default class JobListing extends React.Component {
    constructor (props){
        super(props)
        this.handleChangeFilter = this.handleChangeFilter.bind(this)
        this.datatableRef = React.createRef();
        this.$datatable = null

        this.initialState = {
            Jname:'',
            J_tel:'',
            J_min_salary:'',
            J_max_salary:'',
            PostedDate:'',
            J_hours:'',
            J_email:'',
            J_city:'',
            J_area:'',
            J_Working_Day:'',
            J_Qualification:'',
            J_JpLevel:'',
            J_Description:'',
            J_Contract:'',
            J_Address:'',
        regenerated_jobs : this.props.all_jobs || [],
            showCities : false,
            cities : []
        }
        this.state = this.initialState
    }
    
    componentDidMount() {
        this.initializeDatatable()
    }
    
    initializeDatatable() {
        this.$datatable = $(this.datatableRef.current).DataTable({
            "pagingType": "full",
            "ordering" : false
        });

       
    }
    static async getInitialProps(type){
        let all_jobs = []
        let companies = []
        let cities = []
        let areas = []
        let firebase = loadFirebase()

        const querySnapshotAllJobs = await firebase.firestore().collection('job').get()
        querySnapshotAllJobs.forEach(doc => {
          all_jobs.push(Object.assign(
              {id : doc.id,
            data : doc.data()}
          ))
        })

        const querySnapshotCompanies = await firebase.firestore().collection('employer').get()
        querySnapshotCompanies.forEach(doc => {
          companies.push(Object.assign(
              {id : doc.id,
            data : doc.data()}
          ))
        })

        const querySnapshotCities = await firebase.firestore().collection('city').get()
        querySnapshotCities.forEach(doc => {
          cities.push(Object.assign(
              {id : doc.id,
            data : doc.data()}
          ))
        })

        const querySnapshotAreas = await firebase.firestore().collection('area').get()
        querySnapshotAreas.forEach(doc => {
          areas.push(Object.assign(
              {id : doc.id,
            data : doc.data()}
          ))
        })
        return {all_jobs, companies, cities, areas}
    }

    

    reGenerateJobs = (event) => {
            let jobs = []
            this.setState({[event.target.name] : event.target.value})
            if(event.target.value == "old_to_new")
            {
                
                try{
                    db.collection('job').orderBy('PostedDate').get()
                    .then(snaphsot => {
                        snaphsot.forEach(doc=>{
                            jobs.push(Object.assign({
                                id : doc.id,
                                data : doc.data()
                            }))
                        })
                        this.setState({regenerated_jobs : jobs})
                    })
                }catch(error){
                    console.log(error)
                }
            }else if(event.target.value == "new_to_old")
            {
                try{
                    db.collection('job').orderBy('PostedDate').get()
                    .then(snaphsot => {
                        snaphsot.forEach(doc=>{
                            jobs.push(Object.assign({
                                id : doc.id,
                                data : doc.data()
                            }))
                        })
                        this.setState({regenerated_jobs : jobs.reverse()})
                    })
                }catch(error){
                    console.log(error)
                }
            }else {
                try{
                    db.collection('job').get()
                    .then(snaphsot => {
                        snaphsot.forEach(doc=>{
                            jobs.push(Object.assign({
                                id : doc.id,
                                data : doc.data()
                            }))
                        })
                        this.setState({regenerated_jobs : jobs})
                    })
                }catch(error){
                    console.log(error)
                }
            }
    }

    getCompanyName = (id) => {
        
       const companies = this.props.companies
       let name = ''
       companies.map(company=>{
           if(company.id == id){
               name = company.data.C_Name
           }
       })
       return name
    }

    filterShow = () => {
        $("#filterModal").modal('show')
    }

    quickView = (id) => {
        let job = {}
        try{
            db.collection('job').doc(id).get()
            .then(snapshot => {
                job = snapshot.data()
                this.setState({
                    View_Jname:job.Jname,
            View_J_tel:job.J_tel,
            View_J_min_salary:job.J_min_salary,
            View_J_max_salary:job.J_max_salary,
            View_PostedDate:job.PostedDate,
            View_J_hours:job.J_hours,
            View_J_email:job.J_email,
            View_J_city:job.J_city,
            View_J_area:job.J_area,
            View_J_Working_Day:job.J_Working_Day,
            View_J_Qualification:job.J_Qualification,
            View_J_JpLevel:job.J_JpLevel,
            View_J_Description:job.J_Description,
            View_J_Contract:job.J_Contract,
            View_J_Address:job.J_Address,
                })
            })
            $("#quickView").modal('show')
        }catch(error){
            console.log(error)
        }
        
    }

    applyFilter = () => {
        this.$datatable.clear()
        let jobs = []
        const today = new Date()
        //const today = {seconds: 1595784600, nanoseconds: 790000000}
        console.log(`today is ${today}`)
        console.log(this.state)
        try{
            var query = db.collection('job')
            if(this.state.J_Contract !== ""){
                query = query.where('J_Contract','==',this.state.J_Contract)
            }
            if(this.state.J_area !== ""){
                query = query.where('J_area','==',this.state.J_area)
            }
            if(this.state.J_city !== ""){
                query = query.where('J_city','==',this.state.J_city)
            }
            if(this.state.J_min_salary !== ""){
                query = query.where('J_min_salary','>',parseInt(this.state.J_min_salary))
            }
            if(this.state.J_max_salary !== ""){
                query = query.where('J_max_salary','<',parseInt(this.state.J_max_salary))
            }
           
            if(this.state.J_JpLevel !== ""){
                query = query.where('J_JpLevel','<=',parseInt(this.state.J_JpLevel))
            }

            //    <option value="">All Time</option>
            //    <option value="1">Today</option>
            //    <option value="2">Last 3 Days</option> 
            //    <option value="3">Last 7 Days</option> 
            //    <option value="4">This Month</option> 

            if(this.state.posted_within !== ""){
                switch(this.state.posted_within){
                    case '1' : query = query.where('PostedDate','==',today)
                    case '2' : {
                        query = query.where('PostedDate','>=',new Date(today.setDate(today.getDate()-3)))
                        query = query.where('PostedDate','<=',today)
                    }
                    case '3' : {
                        query = query.where('PostedDate','>=',new Date(today.setDate(today.getDate()-7)))
                        query = query.where('PostedDate','<=',today)
                    }
                    case '4' : {
                        query = query.where('PostedDate','>=',new Date(today.setMonth(today.getMonth()-1)))
                        query = query.where('PostedDate','<=',today)
                    }             
                }
                
            }
            query.get()
            .then(snaphsot => {
                snaphsot.forEach(doc=>{
                    jobs.push(Object.assign({
                        id : doc.id,
                        data : doc.data()
                    }))
                })
                this.setState({regenerated_jobs : jobs})
            })
        }catch(error){
            console.log(error)
        }
    }

    resetFilter = () => {
        this.setState(this.initialState)
        console.log(this.state)
    }

    handleChangeFilter = (event) => {
        event.preventDefault()
        this.setState({[event.target.name] : event.target.value});
            if(event.target.name == "area"){
                this.getCities(event.target.value)
                this.getAreaName(event.target.value)
                this.setState({showCities : true})
            }
            
    }

    getCities = (id) => {
        let cities = []
        try{
            db.collection('city').where('area_id',"==",id).get().
            then((snapshot)=>{
                snapshot.forEach(doc => {
                    cities.push(Object.assign(
                        {id : doc.id,
                      data : doc.data()}
                    ))
                })
                this.setState({cities})
            })
            
        }catch(error){
            console.log(error)
        }
    }

    getAreaName = (id) => {
        let area = {}
        try{
          db.collection('area').doc(id).get()
          .then((snapshot)=>{
              area = snapshot.data();
              this.setState({areaName : area.areaName})
          })
          
      }catch(error){
          console.log(error)
      }
      }
      
    getDateString = (obj) => {
        var t = new Date(1970, 0, 1);
        t.setSeconds(obj.seconds);
        return t.getDate()+'/'+(t.getMonth()+1)+'/'+t.getFullYear()
    }
    getLocation = (city_id,area_id) => {
        
        const cities = this.props.cities
        const areas = this.props.areas
        
        let city_name = ''
        let area_name = ''

        cities.map(city=>{
            if(city.id == city_id){
                city_name = city.data.cityName
            }
        })

        areas.map(area=>{
            if(area.id == area_id){
                area_name = area.data.areaName
            }
        })
       return city_name + ","+ area_name
     }
     render(){
      const all_jobs = this.state.regenerated_jobs
      const areas = this.props.areas
      const cities = this.state.cities
      const viewCities = this.props.cities
      const stateObj = this.state
        return(
            <html>
            <body id="top">
            <nav className="navbar nav-color sticky-top">
            <div className="site-logo col-6"><a href="index" style={{fontSize:30, color: "white"}}>Nursing Job <b style={{color : "rgb(187, 7, 106)"}}>Agency</b></a></div>
            <a href="index">Home</a>
            <a href="#">About</a>
            <a href="contact">Contact</a>
            <a href="filter">Filter</a>
            <button type="button" className="btn btn-float signin" data-toggle="modal" data-target="#myModal" style={{background: "rgb(187, 7, 106)", color: "white"}}>Sign In</button>
            </nav>
         
              <div className="site-wrap">
              
    
                  
              
            
              <section className="section-hero overlay inner-page bg-image" style={{backgroundImage: "url('/images/1.png')"}}id="home-section">
                <br/>
              <h1 className="font-weight-bold text-center" style={{color:"rgba(187, 7, 106)", width:600, margin:"auto"}}>A Powerful Job Agency</h1>
             
              </section>
              <section className="site-section">
                <div className="container">
                <div className="row mb-5 justify-content-center" >
                    <div className="col-md-7 text-center">
                      <h2 className="section-title mb-2"style={{color:"rgba(187, 7, 106)",marginLeft:"center"}}>{`${all_jobs.length} - Jobs Found`}</h2>
                    </div>
                </div>
                <div className="select-job-items">
                                                <span>Sort by</span>
                                                <select name="sortby" style={{marginRight : 1+"em"}} onChange={this.reGenerateJobs}>
                                                    <option value="none">None</option>
                                                    <option value="new_to_old">Newest to Oldest</option>
                                                    <option value="old_to_new">Oldest to Newest</option>
                                                </select>
                </div><br/>
              <table ref={this.datatableRef} style={{width : 100 + "%"}}>
            <thead>
                <tr>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {this.state.regenerated_jobs && this.state.regenerated_jobs.map((job) => (
                    <tr id={job.id} >

                     <td>
                     <div className="row align-items-start job-item  pb-3 mb-3 pt-3">
                      <div className="col-md-2">
                        <a href="job-single"><img src="images/featured-listing-1.jpg" alt="Image" className="img-fluid"/></a>
                      </div>
                      <div className="col-md-4">
                        <span className="badge px-2 py-1 mb-3" style={{background: "rgb(187, 7, 106)", color: "white"}}>{`${job.data.J_Contract}`}</span>
                        <h2><a href="job-single" style={{color:"#3c3c3c",fontWeight:"bold"}}>Full Time Nurse : Hokkaido</a></h2>
                        <p className="meta">Employer: <strong>{this.getCompanyName(job.data.EMPLOYERID)}&nbsp;&nbsp;&nbsp;</strong>Posted In: <strong>{this.getDateString(job.data.PostedDate)}</strong></p>
                      </div>
                      <div className="col-md-3 text-left">
                        <h3 style={{color:"#3c3c3c",fontSize:16}}><strong>{this.getLocation(job.data.J_city,job.data.J_area)}</strong></h3>
                        <span className="meta">Hokkaido</span>
                      </div>
                      <div className="col-md-3 text-md-right">
                        <strong style={{color:"#3c3c3c"}}>200,000 &mdash;  230,000</strong>
                      </div>
                    </div>
                     </td>
                    </tr>
                ) )
                }
            </tbody>
        </table>
                      
        <div className="pagination-area pb-115 text-center ">
            <div className="container ">
                <div className="row ">
                    <div className="col-xl-12 ">
                        <div className="single-wrap d-flex justify-content-center ">
                            <nav aria-label="Page navigation example ">
                                <ul className="pagination justify-content-start ">
                                    <li className="page-item " onClick={this.goFirst}><a className="page-link " >First</a></li>
                                    {this.state.current_start > 0 && (<li className="page-item " onClick={this.backwardPage}><a className="page-link " ><span className="ti-angle-left "></span></a></li>)}
                                    {all_jobs.length >= this.state.current_start && (<li className="page-item " onClick={this.forwardPage}><a className="page-link " ><span className="ti-angle-right "></span></a></li>)}
                                    <li className="page-item " onClick={this.goLast}><a className="page-link " >Last</a></li>
                                    
                                </ul>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>
        </div>
          
          </div></section>  
          
              
                  
              <footer className="site-footer">
          
                
                <div className="container">
                  <div className="row mb-5">
                    <div className="col-6 col-md-3 mb-4 mb-md-0">
                      <h3>Address</h3>
                      <ul className="list-unstyled">
                        <li><a href="#">87,Ward Tyokyo,Japan </a></li>
                        <li><a href="#">nursejobagency@gmail.com</a></li>
                        <li><a href="#">864-11-222</a></li>
                        
                      </ul>
                    </div>
                    <div className="col-6 col-md-3 mb-4 mb-md-0">
                      <h3>Company</h3>
                      <ul className="list-unstyled">
                        <li><a href="#">About Us</a></li>
                        <li><a href="#">Resources</a></li>
                      </ul>
                    </div>
                    <div className="col-6 col-md-3 mb-4 mb-md-0">
                      <h3>Support</h3>
                      <ul className="list-unstyled">
                        <li><a href="#">Support</a></li>
                        <li><a href="#">Privacy</a></li>
                        <li><a href="#">Terms of Service</a></li>
                      </ul>
                    </div>
                    <div className="col-6 col-md-3 mb-4 mb-md-0">
                      <h3>Contact Us</h3>
                      <div className="footer-social">
