import React from 'react';
import Link from 'next/link';
import Head from 'next/head';
import {loadFirebase} from '../lib/db.js';
export default class addJob extends React.Component {
    constructor(){
        super();
        this.initial_state = {
            JobContract : '',
            jobName : '',
            jobArea:''
        };
        this.state = this.initial_state;
    }
    handleChange = (event) =>{
        this.setState({[event.target.name] : event.target.value});
    }
    addJob = event => {
        event.preventDefault();
        let firebase = loadFirebase() 
        const userRef = firebase.firestore().collection('test').add({
            JobContract: this.state.JobContract,
            jobName: this.state.jobName,
            jobArea:this.state.selectedValue
        });        
        this.setState({
          JobContract: '',
          jobName: '',
          jobArea:''
        });
    };
    render() {
    return (
        <body>
        <form action="#" method="post" style={{width: 100+"%"}} onSubmit={this.addJob}>
                                <div className="card-header p-0">
                                    <div className="bg-info text-white py-2" style={{paddingLeft:20}}>
                                        <h3><i className="fa fa-file-alt"></i> Application Requirement</h3>
                                    </div>
                                </div>
                                <div className="card-body p-3">
                                    <div className="row">
                                      <div className="col-sm-6">
                                        <div className="form-group">
                                            <div className="input-group mb-2">
                                                <div className="input-group-prepend">
                                                    <div className="input-group-text"><i className="fa fa-briefcase text-info"></i></div>
                                                </div>
                                                <input type="text" className="form-control" id="JobContract" name="JobContract" placeholder="Enter Job Name" onChange={this.handleChange} value={this.state.JobContract} required />
                                            </div>
                                        </div>
                                      </div>
                                      <div className="col-sm-6">
                                          <div className="form-group">
                                              <div className="input-group mb-2">
                                                  <div className="input-group-prepend">
                                                      <div className="input-group-text"><i className="fa fa-phone text-info"></i></div>
                                                  </div>
                                                  <input type="text" className="form-control" id="jobName" name="jobName" placeholder="Enter Employer Phone Number" onChange={this.handleChange} value={this.state.jobName} />
                                              </div>
                                          </div>
                                        </div>
                                        <div className="col-sm-6">
                                          <div className="form-group">
                                              <div className="input-group mb-2">
                                                  <div className="input-group-prepend">
                                                      <div className="input-group-text"><i className="fa fa-phone text-info"></i></div>
                                                  </div>
                                                  <select className="form-control"name="area" onChange={this.handleChange} value={this.state.selectedValue}>
                                                        <option value="unknown">Select Area</option>
                                                        <option value="1">Hokkaido</option>
                                                        <option value="2">Honshu</option>
                                                        <option value="3">Shikoku</option>
                                                        <option value="4">Kyushu</option>
                                                        <option value="5">Okinawa</option>
                                                        </select>
                                              </div>
                                          </div>
                                        </div>
                                    </div>


                                    
                                    <div className="text-center">
                                        <input type="submit" value="Add" className="btn btn-info btn-width rounded-4 py-2"  />
                                    </div>
                                </div>
                        </form>
                    </body>
    )
    }}
