import React from 'react';
import Link from 'next/link'
import {db} from '../lib/db'
import LayoutWithFooter from '../components/LayoutWithFooter';


export default class JobListing extends React.Component {
    constructor (props){
        super(props)
        this.handleChangeFilter = this.handleChangeFilter.bind(this)
        this.datatableRef = React.createRef();
        this.$datatable = null

        this.initialState = {
            Jname:'',
            J_tel:'',
            J_min_salary:'',
            J_max_salary:'',
            PostedDate:'',
            J_hours:'',
            J_email:'',
            J_city:'',
            J_area:'',
            J_Working_Day:'',
            J_Qualification:'',
            J_JpLevel:'',
            J_Description:'',
            J_Contract:'',
            J_Address:'',
        regenerated_jobs : this.props.all_jobs || [],
            showCities : false,
            cities : []
        }
        this.state = this.initialState
    }
    
    componentDidMount() {
        this.initializeDatatable()
    }
    
    initializeDatatable() {
        this.$datatable = $(this.datatableRef.current).DataTable({
            "pagingType": "full",
            "ordering" : false
        });

       
    }
    static async getInitialProps(type){
        let all_jobs = []
        let companies = []
        let cities = []
        let areas = []

        const querySnapshotAllJobs = await db.collection('job').get()
        querySnapshotAllJobs.forEach(doc => {
          all_jobs.push(Object.assign(
              {id : doc.id,
            data : doc.data()}
          ))
        })

        const querySnapshotCompanies = await db.collection('employer').get()
        querySnapshotCompanies.forEach(doc => {
          companies.push(Object.assign(
              {id : doc.id,
            data : doc.data()}
          ))
        })

        const querySnapshotCities = await db.collection('city').get()
        querySnapshotCities.forEach(doc => {
          cities.push(Object.assign(
              {id : doc.id,
            data : doc.data()}
          ))
        })

        const querySnapshotAreas = await db.collection('area').get()
        querySnapshotAreas.forEach(doc => {
          areas.push(Object.assign(
              {id : doc.id,
            data : doc.data()}
          ))
        })
        return {all_jobs, companies, cities, areas}
    }

    

    reGenerateJobs = (event) => {
            let jobs = []
            this.setState({[event.target.name] : event.target.value})
            if(event.target.value == "old_to_new")
            {
                
                try{
                    db.collection('job').orderBy('PostedDate').get()
                    .then(snaphsot => {
                        snaphsot.forEach(doc=>{
                            jobs.push(Object.assign({
                                id : doc.id,
                                data : doc.data()
                            }))
                        })
                        this.setState({regenerated_jobs : jobs})
                    })
                }catch(error){
                    console.log(error)
                }
            }else if(event.target.value == "new_to_old")
            {
                try{
                    db.collection('job').orderBy('PostedDate').get()
                    .then(snaphsot => {
                        snaphsot.forEach(doc=>{
                            jobs.push(Object.assign({
                                id : doc.id,
                                data : doc.data()
                            }))
                        })
                        this.setState({regenerated_jobs : jobs.reverse()})
                    })
                }catch(error){
                    console.log(error)
                }
            }else {
                try{
                    db.collection('job').get()
                    .then(snaphsot => {
                        snaphsot.forEach(doc=>{
                            jobs.push(Object.assign({
                                id : doc.id,
                                data : doc.data()
                            }))
                        })
                        this.setState({regenerated_jobs : jobs})
                    })
                }catch(error){
                    console.log(error)
                }
            }
    }

    getCompanyName = (id) => {
        
       const companies = this.props.companies
       let name = ''
       companies.map(company=>{
           if(company.id == id){
               name = company.data.C_Name
           }
       })
       return name
    }

    filterShow = () => {
        $("#filterModal").modal('show')
    }

    quickView = (id) => {
        let job = {}
        try{
            db.collection('job').doc(id).get()
            .then(snapshot => {
                job = snapshot.data()
                this.setState({
                    View_Jname:job.Jname,
            View_J_tel:job.J_tel,
            View_J_min_salary:job.J_min_salary,
            View_J_max_salary:job.J_max_salary,
            View_PostedDate:job.PostedDate,
            View_J_hours:job.J_hours,
            View_J_email:job.J_email,
            View_J_city:job.J_city,
            View_J_area:job.J_area,
            View_J_Working_Day:job.J_Working_Day,
            View_J_Qualification:job.J_Qualification,
            View_J_JpLevel:job.J_JpLevel,
            View_J_Description:job.J_Description,
            View_J_Contract:job.J_Contract,
            View_J_Address:job.J_Address,
                })
            })
            $("#quickView").modal('show')
        }catch(error){
            console.log(error)
        }
        
    }

    applyFilter = () => {
        this.$datatable.clear()
        let jobs = []
        const today = new Date()
        //const today = {seconds: 1595784600, nanoseconds: 790000000}
        console.log(`today is ${today}`)
        console.log(this.state)
        try{
            var query = db.collection('job')
            if(this.state.J_Contract !== ""){
                query = query.where('J_Contract','==',this.state.J_Contract)
            }
            if(this.state.J_area !== ""){
                query = query.where('J_area','==',this.state.J_area)
            }
            if(this.state.J_city !== ""){
                query = query.where('J_city','==',this.state.J_city)
            }
            if(this.state.J_min_salary !== ""){
                query = query.where('J_min_salary','>',parseInt(this.state.J_min_salary))
            }
            if(this.state.J_max_salary !== ""){
                query = query.where('J_max_salary','<',parseInt(this.state.J_max_salary))
            }
           
            if(this.state.J_JpLevel !== ""){
                query = query.where('J_JpLevel','<=',parseInt(this.state.J_JpLevel))
            }

            //    <option value="">All Time</option>
            //    <option value="1">Today</option>
            //    <option value="2">Last 3 Days</option> 
            //    <option value="3">Last 7 Days</option> 
            //    <option value="4">This Month</option> 

            if(this.state.posted_within !== ""){
                switch(this.state.posted_within){
                    case '1' : query = query.where('PostedDate','==',today)
                    case '2' : {
                        query = query.where('PostedDate','>=',new Date(today.setDate(today.getDate()-3)))
                        query = query.where('PostedDate','<=',today)
                    }
                    case '3' : {
                        query = query.where('PostedDate','>=',new Date(today.setDate(today.getDate()-7)))
                        query = query.where('PostedDate','<=',today)
                    }
                    case '4' : {
                        query = query.where('PostedDate','>=',new Date(today.setMonth(today.getMonth()-1)))
                        query = query.where('PostedDate','<=',today)
                    }             
                }
                
            }
            query.get()
            .then(snaphsot => {
                snaphsot.forEach(doc=>{
                    jobs.push(Object.assign({
                        id : doc.id,
                        data : doc.data()
                    }))
                })
                this.setState({regenerated_jobs : jobs})
            })
        }catch(error){
            console.log(error)
        }
    }

    resetFilter = () => {
        this.setState(this.initialState)
        console.log(this.state)
    }

    handleChangeFilter = (event) => {
        event.preventDefault()
        this.setState({[event.target.name] : event.target.value});
            if(event.target.name == "area"){
                this.getCities(event.target.value)
                this.getAreaName(event.target.value)
                this.setState({showCities : true})
            }
            
    }

    getCities = (id) => {
        let cities = []
        try{
            db.collection('city').where('area_id',"==",id).get().
            then((snapshot)=>{
                snapshot.forEach(doc => {
                    cities.push(Object.assign(
                        {id : doc.id,
                      data : doc.data()}
                    ))
                })
                this.setState({cities})
            })
            
        }catch(error){
            console.log(error)
        }
    }

    getAreaName = (id) => {
        let area = {}
        try{
          db.collection('area').doc(id).get()
          .then((snapshot)=>{
              area = snapshot.data();
              this.setState({areaName : area.areaName})
          })
          
      }catch(error){
          console.log(error)
      }
      }
      
    getDateString = (obj) => {
        var t = new Date(1970, 0, 1);
        t.setSeconds(obj.seconds);
        return t.getDate()+'/'+(t.getMonth()+1)+'/'+t.getFullYear()
    }
    getLocation = (city_id,area_id) => {
        
        const cities = this.props.cities
        const areas = this.props.areas
        
        let city_name = ''
        let area_name = ''

        cities.map(city=>{
            if(city.id == city_id){
                city_name = city.data.cityName
            }
        })

        areas.map(area=>{
            if(area.id == area_id){
                area_name = area.data.areaName
            }
        })
       return city_name + "," + area_name
     }
    render (){
        const all_jobs = this.state.regenerated_jobs
        const areas = this.props.areas
        const cities = this.state.cities
        const viewCities = this.props.cities
        const stateObj = this.state
        return (
            
        <LayoutWithFooter title="Job List">
        <div className="modal fade" id="filterModal" tabindex="-1" role="dialog" aria-hidden="true">
        <div className="modal-dialog" role="document">
            <div className="modal-content">
            <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Filter Jobs</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div className="modal-body">
                <div className="job-category-listing mb-50">
                    <div className="single-listing pb-50">

                        <div className="small-section-tittle2">
                            <h4>Job Area</h4>
                        </div>
                        <div className="select-job-items2">
                            <select name="area" className="form-control" value={this.state.area} onChange={this.handleChangeFilter}>
                                <option value="">Any</option>
                                {areas && areas.map(area => 
                                    (<option value={area.id}>{area.data.areaName}</option>)
                                )}
                            </select>
                        </div>
                    </div>
                    
                    <div className="single-listing">
                        {this.state.showCities && (
                            <React.Fragment>
                                <div className="small-section-tittle2">
                                    <h4>{`Cities in ${this.state.areaName}`}</h4>
                                </div>
                                <div className="select-job-items2 pb-50">
                                    <select name="city" className="form-control" value={this.state.city} onChange={this.handleChangeFilter}>
                                    <option value="">Any</option>
                                    {cities && cities.map(city => 
                                            (<option value={city.id}>{city.data.cityName}</option>)
                                        )}
                                    </select>
                                </div>
                            </React.Fragment>
                        )}
                        

                        <div className="select-Categories pb-50">
                            <div className="small-section-tittle2">
                                <h4>Employment Type</h4>
                            </div>
                            <div className="select-job-items2">
                            <select name="J_Contract" className="form-control" value={this.state.J_Contract} onChange={this.handleChangeFilter}>
                                <option value="" >Any</option>
                                <option value="Full">Full Time</option>
                                <option value="Part">Part Time</option>
                            </select>
                            </div>
                        </div>
                    </div>
                    
                    <div className="single-listing">
                        <div className="select-Categories pb-50">
                            <div className="small-section-tittle2">
                                <h4>Minimum Experience</h4>
                            </div>
                           
                        </div>
                    </div>
                    <div className="single-listing">
                        <div className="select-Categories pb-50">
                            <div className="small-section-tittle2">
                                <h4>Minimum Japanese Skill</h4>
                            </div>
                            <select name="J_JpLevel" className="form-control" value={this.state.J_JpLevel} onChange={this.handleChangeFilter}>
                                <option value="" >Any</option>
                                <option value="3">N3</option>
                                <option value="2">N2</option>
                                <option value="1">N1</option>  
                            </select>
                        </div>
                    </div>
                    <div className="single-listing">
                        <div className="select-Categories pb-50">
                            <div className="small-section-tittle2">
                                <h4>Posted Within</h4>
                            </div>
                            <select name="PostedDate" className="form-control" value={this.state.PostedDate} onChange={this.handleChangeFilter}>
                                <option value="">All Time</option>
                                <option value="1">Today</option>
                                <option value="2">Last 3 Days</option> 
                                <option value="3">Last 7 Days</option> 
                                <option value="4">This Month</option>  

                            </select>
                        </div>
                    </div>
                    <div className="single-listing">
                        <aside className="left_widgets p_filter_widgets price_rangs_aside sidebar_box_shadow">
                            <div className="small-section-tittle2">
                                <h4>SaLary Range</h4>
                            </div>
                            <div className="widgets_inner">
                                <div className="range_item">
                                    <div className="align-items-center">
                                        <div className="price_text">
                                            <p>from :</p>
                                        </div>
                                        <div className="justify-content-center">
                                            <input type="number" className="form-control" id="J_min_salary" name="J_min_salary" value={this.state.min_salary} onChange={this.handleChangeFilter} />
                                            <span>to</span>
                                            <input type="number" className="form-control" id="J_max_salary" name="J_max_salary" value={this.state.max_salary} onChange={this.handleChangeFilter} />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </aside>
                    </div>
                </div>
            </div>
            
            <div className="modal-footer">
                <button type="button" className="btn" onClick={this.resetFilter} aria-label="Cancel">Reset Filter</button>  
                <button type="button" className="btn" data-dismiss="modal" onClick={this.applyFilter}>Apply Filter</button>
            </div>
            </div>
        </div>
        </div>

        <div className="modal fade" id="quickView" tabindex="-1" role="dialog" aria-hidden="true">
        <div className="modal-dialog modal-lg">
            <div className="modal-content">
            <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Quick View</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div className="modal-body">

            <form className="register_form">
                <div className="form-group">
                <label for="title">Job Title</label>
                    <div className="input-group-icon mt-10">
                        <div className="icon "><i className="fa fa-briefcase" aria-hidden="true "></i></div>
                        <input type="text" value={this.state.View_Jname} disabled className="single-input"/>
                    </div>
                </div>

                <div className="container to_up">
                    
                    <div className="row">
                    <div className="form-group two_col_input_left">
                    <label for="min_salary">Minimum Salary</label>
                    <div className="input-group-icon mt-10 ">
                            <div className="icon "><i className="fa fa-yen-sign" aria-hidden="true "></i></div>
                            <input type="number" value={this.state.View_J_min_salary} disabled className="single-input"/>

                        </div>
                    </div>
                    <div className="form-group two_col_input">
                    <label for="max_salary">Maximum Salary</label>
                    <div className="input-group-icon mt-10 ">
                            <div className="icon "><i className="fa fa-yen-sign" aria-hidden="true "></i></div>
                            <input type="number" value={this.state.view_J_max_salary} disabled className="single-input"/>
                        </div>
                    </div>
                        
                    </div>
                </div>

                <div className="form-group to_up">
                <label for="employment_type">Employment Type</label>
                <div className="input-group-icon mt-10 ">
                <div className="icon "><i className="fas fa-list " aria-hidden="true "></i></div>
                <select className="form-control single-input select_border" disabled>
                 
                        <option selected={this.state.View_J_Contract == "Full" ? ("selected") : ("false")} disabled>Full Time</option>
                        <option selected={this.state.View_J_Contract == "Part" ? ("selected") : ("false")} disabled>Part Time</option>   
                </select>
                </div>
                </div>

               

                
                <div className="form-group to_up">
                <label for="work_day">Work Days</label>
                <div className="input-group-icon mt-10">
                    <div className="icon "><i className="fa fa-calendar-alt " aria-hidden="true "></i></div>
                    <textarea  cols="30" rows="4"  value={this.state.View_J_Working_Day} className="single-input" disabled></textarea>
                </div>
                </div>
                <div className="form-group to_up">
                <label for="work_hour">Work Hours</label>
                <div className="input-group-icon mt-10">
                    <div className="icon "><i className="fa fa-clock " aria-hidden="true "></i></div>
                    <textarea cols="30" rows="4" value={this.state.View_J_hours} className="single-input" disabled></textarea>
                </div>
                </div>
                <div className="form-group to_up">
                <label for="min_lang_skill">Minimum Japanese Language Skill</label>
                <div className="input-group-icon mt-10 ">
                <div className="icon "><i className="fas fa-language " aria-hidden="true "></i></div>
                <select className="form-control single-input select_border" disabled>
                        <option selected={this.state.view_employment_type == "3" ? ("selected") : ("false")} disabled>N3</option>
                        <option selected={this.state.view_employment_type == "2" ? ("selected") : ("false")} disabled>N2</option>
                        <option selected={this.state.view_employment_type == "1" ? ("selected") : ("false")} disabled>N1</option>  
                </select>
                </div>
                </div>
                <div className="form-group to_up">
                <label for="min_exp_year">Minimum Experience Years</label>
                <div className="input-group-icon mt-10 ">
                <div className="icon "><i className="fas fa-calendar-check " aria-hidden="true "></i></div>
                <select className="form-control single-input select_border" disabled>
                        <option selected={this.state.view_employment_type == "1" ? ("selected") : ("false")} disabled>Less Than 1 Year</option>
                        <option selected={this.state.view_employment_type == "2" ? ("selected") : ("false")} disabled>1-2 Years</option>
                        <option selected={this.state.view_employment_type == "3" ? ("selected") : ("false")} disabled>2-3 Years</option>
                        <option selected={this.state.view_employment_type == "4" ? ("selected") : ("false")} disabled>3-6 Years</option>
                        <option selected={this.state.view_employment_type == "5" ? ("selected") : ("false")} disabled>6 Years and more</option> 
                </select>
                </div>
                </div>
                <div className="form-group to_up">
                <label for="area">Area</label>
                <div className="input-group-icon mt-10 ">
                    <div className="icon "><i className="fas fa-map-marker-alt " aria-hidden="true "></i></div>
                        <select className="form-control single-input select_border" disabled>
                            
                            {areas && areas.map(area => (
                            this.state.view_area == area.id ? <option selected disabled>{area.data.areaName}</option> : <option value={area.id}>{area.data.areaName}</option>
                            ))}
                        </select>
                </div>
                </div>
                <div className="form-group to_up">
                <label for="city">City</label>
                <div className="input-group-icon mt-10 ">
                    <div className="icon "><i className="fas fa-map-marker-alt " aria-hidden="true "></i></div>
                    <select className="form-control single-input select_border" disabled>
                    {
                    viewCities && viewCities.map(city => (
                        this.state.view_city == city.id ? <option selected disabled>{city.data.cityName}</option> : <option value={city.id}>{city.data.cityName}</option>
                    ))
                    }
                </select>
                </div>
                </div>

                <div className="form-group to_up">
                <label for="job_address">Job Address</label>
                <div className="input-group-icon mt-10">
                    <div className="icon "><i className="fa fa-home " aria-hidden="true "></i></div>
                    <textarea cols="30" rows="4"  value={this.state.View_J_Address} className="single-input" disabled></textarea>
                </div>
                </div>    

                <div className="form-group to_up">
                <label for="description">Description</label>
                <div className="input-group-icon mt-10">
                    <div className="icon "><i className="fa fa-tasks " aria-hidden="true "></i></div>
                    <textarea cols="30" rows="4"  value={this.state.View_J_Description} className="single-input" disabled></textarea>
                </div>
                </div>
                
                <div className="form-group to_up">
                <label for="other_qualification">Other Qualifications</label>
                <div className="input-group-icon mt-10">
                    <div className="icon "><i className="fa fa-tasks " aria-hidden="true "></i></div>
                    <textarea cols="30" rows="4"  value={this.state.view_J_Qualification} className="single-input" disabled></textarea>
                </div>
                </div>
               

            </form>
            </div>
        </div>
        </div>
        </div>



        <div className="job-listing-area pt-120 pb-120">
            <div className="container">
                <div className="row">
                    
                    <div className="col-xl-12 col-lg-12 col-md-8">
                        <section className="featured-job-area">
                            <div className="container">
                                <div className="row">
                                    <div className="col-lg-12">
                                        <div className="count-job mb-35">
                                        <a onClick={this.filterShow} className="filter_btn"><img style={{width : 1+"em",height : 1+"em", marginRight : 1+"em"}} src="/assets/img/logo/filter.png"></img>Filter jobs</a>
                                           
                                            
                                            <h5>{`${all_jobs.length} - Jobs Found`}</h5>
                                            
                                            <div className="select-job-items">
                                                <span>Sort by</span>
                                                <select name="sortby" style={{marginRight : 1+"em"}} onChange={this.reGenerateJobs}>
                                                    <option value="none">None</option>
                                                    <option value="new_to_old">Newest to Oldest</option>
                                                    <option value="old_to_new">Oldest to Newest</option>
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                
            <table ref={this.datatableRef} style={{width : 100 + "%"}}>
            <thead>
                <tr>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {this.state.regenerated_jobs && this.state.regenerated_jobs.map((job) => (
                    <tr id={job.id} >

<td className="single-job-items " style={{paddingBottom : 50+"px",paddingTop : 50+"px"}}>
                    <div className="job-items">
                        <div className="company-img">
                            <Link href={`/job_detail?id=${job.id}`}><a><img src="/assets/img/icon/featured-listing-3.png" alt=""/></a></Link>
                        </div>
                        <div className="job-tittle job-tittle2">
                            <Link href={`/job_detail?id=${job.id}`}><a>
                                <h4>{job.data.title}</h4>
                            </a></Link>
                            <ul>
                                <li><i className="fas fa-building "></i>{this.getCompanyName(job.data.EMPLOYERID)}</li>
                                <li><i className="fas fa-map-marker-alt "></i>{this.getLocation(job.data.J_city,job.data.J_area)}</li>

                            </ul>
                            <ul>
                                <li><i className="fas fa-calendar-alt "></i>{this.getDateString(job.data.PostedDate)}</li>
                                <li><i className="fas fa-yen-sign "></i>{`${job.data.J_min_salary} ~ ${job.data.J_max_salary}`}</li>
                                <li><i className="fas fa-clock "></i>{`${job.data.J_Contract}`}
                                </li>
                            </ul>
                        </div>
                    </div>
                    <div className="items-link items-link2 f-right ">
                        <a onClick={()=>this.quickView(job.id)} style={{cursor : "pointer"}}>Quick View</a>
                        <Link href={`/job_detail?id=${job.id}`}><a>View Details</a></Link>
                    </div>
                </td>
                    </tr>
                ) )
                }
            </tbody>
        </table>
                                
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        </div>
        <div className="pagination-area pb-115 text-center ">
            <div className="container ">
                <div className="row ">
                    <div className="col-xl-12 ">
                        <div className="single-wrap d-flex justify-content-center ">
                            <nav aria-label="Page navigation example ">
                                <ul className="pagination justify-content-start ">
                                    <li className="page-item " onClick={this.goFirst}><a className="page-link " >First</a></li>
                                    {this.state.current_start > 0 && (<li className="page-item " onClick={this.backwardPage}><a className="page-link " ><span className="ti-angle-left "></span></a></li>)}
                                    {all_jobs.length >= this.state.current_start && (<li className="page-item " onClick={this.forwardPage}><a className="page-link " ><span className="ti-angle-right "></span></a></li>)}
                                    <li className="page-item " onClick={this.goLast}><a className="page-link " >Last</a></li>
                                    
                                </ul>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        </LayoutWithFooter>
        
        )
    }
}
