import React from 'react';
import Link from 'next/link';
import Head from 'next/head';
import {loadFirebase} from '../lib/db.js'
export default class index extends React.Component {
    constructor(props){
        super(props);
        this.initial_state = {
            JobContract : '',
            jobName : ''
        };
        this.state = this.initial_state;
    }
    handleSubmit = (event) => {
        event.preventDefault();
        const user = this.state; 
      
            try {
                db.collection("test").add(
                    {JobContract : user.JobContract,
                     jobName : user.jobName
                    }
                )
                console.log(this.state);
            }catch(error){
                console.log(error);
            }
            this.state = this.initial_state;
            console.log(this.state);
        }

      handleChange = (event) =>{
          this.setState({[event.target.name] : event.target.value});
      }
    
 
  render() {
    return (
    <html>
      <Head>
        <title>Job Seeker-Home</title>
    </Head>
    <body>
   
  <form>
  <input type="text"   onChange={this.handleChange} name="JobContract" className="form-control"/><br/><br/>
  <label for="lname">Last name:</label>
  <input type="text"   onChange={this.handleChange} name="jobName" className="form-control"/><br/><br/>
  <button className="btn btn-primary" onClick={this.handleSubmit}> Submit</button>
  </form>

    </body>
    </html>
    )
  }
}
